mapper.select:
    select  from APP_INSTALL_PACKAGE

       INSTALL_STATE = #{installState,jdbcType=VARCHAR}

        AND ID = #{id,jdbcType=VARCHAR}


        AND APP_INFO_ID = #{appInfoId,jdbcType=VARCHAR}


        AND USER_ID = #{userId,jdbcType=VARCHAR}


        AND USER_NAME = #{userName,jdbcType=VARCHAR}


        AND INSTALL_ALIAS = #{installAlias,jdbcType=VARCHAR}


        AND INSTALL_UUID_NAME = #{installUuidName,jdbcType=VARCHAR}


        AND INSTALL_VERSION = #{installVersion,jdbcType=VARCHAR}


        AND INSTALL_ADDRESS = #{installAddress,jdbcType=VARCHAR}


        AND INSTALL_WHERE_DISK = #{installWhereDisk,jdbcType=VARCHAR}


        AND INSTALL_SIZE = #{installSize,jdbcType=VARCHAR}


        AND DETAILS = #{details,jdbcType=VARCHAR}


        AND UPLOAD_TIME = #{uploadTime,jdbcType=TIMESTAMP}


        AND AUDIT_RESULT = #{auditResult,jdbcType=VARCHAR}


        AND DOWNLOADS_NUM = #{downloadsNum,jdbcType=DECIMAL}


        AND INSTALL_STATE = #{installState,jdbcType=VARCHAR}



        AND ID IN

	         #{item,jdbcType=VARCHAR}




      ORDER BY ${order} ${sort}
