mapper.select:
    select  from APP_INFO

       APP_STATE = #{appState,jdbcType=VARCHAR}

        AND ID = #{id,jdbcType=VARCHAR}


        AND APP_CATEGORY_ID = #{appCategoryId,jdbcType=VARCHAR}


        AND APP_TYPE = #{appType,jdbcType=VARCHAR}


        AND APP_NAME = #{appName,jdbcType=VARCHAR}


        AND CURRENT_VERSION = #{currentVersion,jdbcType=VARCHAR}


        AND APP_DETAILS = #{appDetails,jdbcType=VARCHAR}


        AND CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        AND AUDIT_RESULT = #{auditResult,jdbcType=VARCHAR}


        AND BROWSE_NUM = #{browseNum,jdbcType=DECIMAL}


        AND DOWNLOADS_NUM = #{downloadsNum,jdbcType=DECIMAL}


        AND INSTPACK_NUM = #{instpackNum,jdbcType=DECIMAL}


        AND APP_STATE = #{appState,jdbcType=VARCHAR}



        AND ID IN

	         #{item,jdbcType=VARCHAR}




    	 ORDER BY ${order} ${sort}
