mapper.select:
    select  from APP_PICTURE

      PICTURE_STATE = #{pictureState,jdbcType=VARCHAR}

        AND ID = #{id,jdbcType=VARCHAR}


        AND APP_INFO_ID = #{appInfoId,jdbcType=VARCHAR}


        AND APP_INSTALL_ID = #{appInstallId,jdbcType=VARCHAR}


        AND PICTURE_ADDERSS = #{pictureAdderss,jdbcType=VARCHAR}


        AND PICTURE_WHERE_DISK = #{pictureWhereDisk,jdbcType=VARCHAR}


        AND PICTURE_ALIAS = #{pictureAlias,jdbcType=VARCHAR}


        AND PICTURE_UUID_NAME = #{pictureUuidName,jdbcType=VARCHAR}


        AND PICTURE_SIZE = #{pictureSize,jdbcType=VARCHAR}


        AND AUDIT_RESULT = #{auditResult,jdbcType=VARCHAR}


        AND PICTURE_STATE = #{pictureState,jdbcType=VARCHAR}



        AND ID IN

	         #{item,jdbcType=VARCHAR}




    	 ORDER BY ${order} ${sort}
