mapper.select:
select  from T_BIS_STUDENT

  
    AND ID = #{id,jdbcType=VARCHAR}
  
  
    AND SNO = #{sno,jdbcType=VARCHAR}
  
  
    AND NAME = #{name,jdbcType=VARCHAR}
  
  
    AND SEX = #{sex,jdbcType=VARCHAR}
  
  
    AND BIRTHDAY = #{birthday,jdbcType=VARCHAR}
  
  
    AND NATION_CODE = #{nationCode,jdbcType=VARCHAR}
  
  
    AND IDEN_ID = #{idenId,jdbcType=VARCHAR}
  
  
    AND NATIVE_PLACE_ID = #{nativePlaceId,jdbcType=VARCHAR}
  
  
    AND AREA_ID = #{areaId,jdbcType=VARCHAR}
  
  
    AND POLITICAL_ID = #{politicalId,jdbcType=VARCHAR}
  
  
    AND COLLEGE_ID = #{collegeId,jdbcType=VARCHAR}
  
  
    AND MAJOR_ID = #{majorId,jdbcType=VARCHAR}
  
  
    AND ENTRANCE_GRADE = #{entranceGrade,jdbcType=VARCHAR}
  
  
    AND CLASSNAME = #{classname,jdbcType=VARCHAR}
  
  
    AND ADRESS = #{adress,jdbcType=VARCHAR}
  
  
    AND PHONE = #{phone,jdbcType=VARCHAR}
  
  
    AND EMAIL = #{email,jdbcType=VARCHAR}
  
  
    AND INSTRUCTOR_ID = #{instructorId,jdbcType=VARCHAR}
  
  
    AND ONE_CARD = #{oneCard,jdbcType=VARCHAR}
  
  
    AND BANK_ACCOUNT = #{bankAccount,jdbcType=VARCHAR}
  
  
    AND IMAGE = #{image,jdbcType=VARCHAR}
  
  
   AND ID IN 
      
    #{item,jdbcType=VARCHAR}
     
  

  
  ORDER BY ${order} ${sorting}
